import { createContext, useState } from "react";
import axios from 'axios'



export const StoreContext = createContext(null);




const StoreContextProvider = (props) => {
  const [cartitem, setCarttitem] = useState({})
  const [token, setToken] = useState()
  const [food_list, setFoodlist] = useState([])
  const addtocart = (itemId) => {
    if (!cartitem[itemId]) {
      setCarttitem((prev) => ({ ...prev, [itemId]: 1 }))
    }
    else {
      setCarttitem((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }))
    }
  }
  const remove = (itemId) => {
    setCarttitem((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }))
  }
  const getTotal = () => {
    let Total = 0
    for (const item in cartitem) {
      if (cartitem[item] > 0) {
        let itemInfo = food_list.find((product) => product._id === item)
        Total += itemInfo.price * cartitem[item]
      }

    }
    return Total;
  }
  const contextvalue = {
    food_list,
    cartitem, setCarttitem, addtocart, remove, getTotal, token, setToken

  }
  const fetchfoodlist = async() => {
    const response = await axios.get("http://localhost:5000/list")

    if (response.data.success) {
      setList(response.data.data)
    }
    else {
      toast.error("error")
    }



  }

  return (
    <StoreContext.Provider value={contextvalue}>
      {props.children}
    </StoreContext.Provider>
  )
}
export default StoreContextProvider;
