import React, { useContext } from 'react'
import { StoreContext } from '../Menu/CONTEXT/StoreContext'

const Cart = ({id,name,price,description,image}) => {
  const {cartitem,remove,food_list}=useContext(StoreContext)
  
  return (
    <div className='cart'>
      <div className='cart1'>
        <div className='cart2'>
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>


        </div>
        <br/>
        <hr/>
        {food_list.map((item,index)=>{
           if(item)
          { return(
          <div>
            <p>{cartitem[id]}</p>
        
          </div>)
          }

        })}

      </div>

    </div>
  )
}

export default Cart
