import { createContext, useEffect, useState } from "react";
import axios from 'axios'




export const StoreContext = createContext(null);




const StoreContextProvider = (props) => {
  const [cartitem, setCarttitem] = useState({})
  const [token, setToken] = useState("")
  const [food_list, setFoodlist] = useState([])
  const addtocart = (itemId) => {
    if (!cartitem[itemId]) {
      setCarttitem((prev) => ({ ...prev, [itemId]: 1 }))
    }
    else {
      setCarttitem((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }))
    }
    if(token){
      axios.post("http://localhost:5000/add",{itemId},{headers:{token}})
}
  }
  const remove = async (itemId) => {
    setCarttitem((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }))
    if(token){
      axios.post("http://localhost:5000/remove",{itemId},{headers:{token}})
    }
  }

  const getTotal = () => {
    let Total = 0
    for (const item in cartitem) {
      if (cartitem[item] > 0) {
        let itemInfo = food_list.find((product) => product._id === item)
        Total += itemInfo.price * cartitem[item]
      }

    }
    return Total;
  }
  const url="http://localhost:5000"
  const contextvalue = {
    food_list,
    cartitem, setCarttitem, addtocart, remove, getTotal, token, setToken,url

  }
  const fetchfoodlist = async() => {
    const res = await axios.get("http://localhost:5000/list")

       setFoodlist(res.data.data)



  }
 
  
  useEffect(()=>{
    async function loaddata(){
      await fetchfoodlist()
      if(localStorage.getItem("token")){
        setToken(localStorage.getItem("token"))
        await loadcartdata(localStorage.getItem("token"))
      }
     
    }
    loaddata()
  },[])
  const loadcartdata =async (token)=>{
    const res= axios.get("http://localhost:5000/get")
    .then(result => setCarttitem(result.data))
    .catch(err => console.log(err));
  }

  return (
    <StoreContext.Provider value={contextvalue}>
      {props.children}
    </StoreContext.Provider>
  )
}
export default StoreContextProvider;
