import React, { useState, useEffect } from 'react';
import './Menu.css';
import { menu_list } from '../assets/assets';
import AOS from 'aos';
import 'aos/dist/aos.css';
import ReactLoading from 'react-loading';

const Menu = ({ category, setCategory }) => {
  const [loading, setLoading] = useState(true); 
  const color = "#ffffff"; 
  const override = `
    display: block;
    margin: 0 auto;
    border-color: red;
  `; 

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  const handleCategoryClick = (itemName) => {
    setCategory(prev => prev === itemName ? "All" : itemName);
  };

  function lod(){
     setTimeout(() => {
      setLoading(false);
    }, 3000);
  }

  return (
    <div className='menu'>
      <h1>Explore our menu</h1>
      <p> Choose from a diverse menu featuring a delectable array of dishes. Our mission is to satisfy your cravings and elevate your dining experience, one delicious meal at a time.</p>
      <div className='menu1'>
        {loading ? (
          <ReactLoading
            type={'spin'}
            color={color}
            height={150}
            width={150}
            className='react-loading'
          />
        ) : (
          menu_list.map((item, index) => (
            <div key={index} data-aos="fade-left" className='menu2' onClick={() => handleCategoryClick(item.menu_name)}>
              <img onClick={lod} className={category === item.menu_name ? "active" : ""} src={item.menu_image} alt={item.menu_name} />
              <h5>{item.menu_name}</h5>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Menu;
