import React, { useContext } from 'react'
import { StoreContext } from '../Menu/CONTEXT/StoreContext'

const Cart = () => {
  const {cartItems,food_list,removeFromCart}=useContext(StoreContext)
  return (
    <div className='cart'>
      <div className='cart1'>
        <div className='cart2'>
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>


        </div>
        <br/>
        <hr/>
        {food_list.map((item)=>{
          if(cartItems[item._id]>0){
            return(
              <div className='cart 3'>
                <p>{item.name}</p>
            </div>)
          }

        })}

      </div>

    </div>
  )
}

export default Cart
