import React, { useContext } from 'react'
import { StoreContext } from '../Menu/CONTEXT/StoreContext'

const Cart = () => {
  const {cartitem,remove,food_list}=useContext(StoreContext)
  console.log(food_list)
  console.log("af",food_list.image)
  console.log("lc",cartitem)
  return (
    <div className='cart'>
      <div className='cart1'>
        <div className='cart2'>
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>


        </div>
        <br/>
        <hr/>
        {food_list.map((item,index)=>{
           if(cartitem[item._id]>0)
          {
            return (
              <div className='cart3'>
                <img src={item.image}/>
                <p>{item.name}</p>
                <p>{item.price}</p>
            </div>)
          }

        })}

      </div>

    </div>
  )
}

export default Cart
