import React, { useContext } from 'react'
import { food_list } from '../../assets/assets'
import './Context.css'

import AOS from 'aos';
import 'aos/dist/aos.css';
import { useEffect } from 'react';
import { assets } from '../../assets/assets';
import Add from './Add';
const Context = ({ Context,addtocart,count,remove }) => {

    useEffect(() => {
        AOS.init({ duration: 1000 });
    }, []);
const id=food_list.id



    return (
        <main>
            <h2>Top dishes near you</h2>
            <div className="container mt-5">
                <div className="row">
                    {food_list.map((item, index) => (

                        <div key={index} className="col-lg-4 col-md-6 col-sm-12 pizzacard" data-aos="fade-up">
                            <div className='m-3 rounded p-3'>
                                <div className='d-flex justify-content-center align-items-center' data-aos="zoom-in">
                                    <div className='m3 rounded p-1'>
                                        <div>
                                            <div className='food2'>

                                                <img className='img1' src={item.image} />
                                                {!count[id]?
                                                    <img className='img3' src={assets.add_icon_white} onClick={() => addtocart(id)} />
                                                    : <div className='add'> <img onClick={() => remove(id)} src={assets.remove_icon_red} />
                                                        <p>{count[id]}</p>

                                                        <img onClick={() => addtocart(id)} src={assets.add_icon_green} /></div>}
                                                <p className='p1'>{item.name}</p>
                                                <img className='img2' src={assets.rating_starts} />
                                                <p className='p2'>{item.description}</p>
                                                <h4> Price:{item.price}-/</h4>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </main>
    );
}





export default Context
